	int i;
	setbgcolor(0);
	clearscreen();
	putimagerle(tittle, 0, 90, 128, 24);
	putimagerle(eyes, 16, 48, 96, 16);
	for(i = 0; i < 31; i++){
		getsprite(i, snow);
		spritesetvalue(i, S_WIDTH, 2);
		spritesetvalue(i, S_HEIGHT, 2);
		spritesetvalue(i, S_SPEEDY, 2 + random(6));
		putsprite(i, random(125), random(32));
	}
	settimer(0, 1000);
	while(gettimer(0)){}
