void init(){
	int i;
	drawTittle();
	ang = 1;
	thislevel = 0;
	getsprite(1, person);
	spritesetvalue(1, S_WIDTH, 8);
	spritesetvalue(1, S_HEIGHT, 12);
	spritesetvalue(1,S_GRAVITY,5);
	spritesetvalue(1,S_SOLID,1);
	spritesetvalue(1,S_ON_COLLISION,personOnCollision);
	getsprite(0, keyspr);
	spritesetvalue(0, S_WIDTH, 8);
	spritesetvalue(0, S_HEIGHT, 6);
	spritesetvalue(0,S_SOLID,1);
	putsprite(1, x, y);
	for(i = 2; i < 8; i++){
		getsprite(i, zombie[0]);
		spritesetvalue(i, S_WIDTH, 8);
		spritesetvalue(i, S_HEIGHT, 12);
		spritesetvalue(i,S_GRAVITY,4);
		spritesetvalue(i,S_SOLID,1);
		spritesetvalue(i,S_ON_COLLISION,zombieOnCollision);
	}
	for(i = 8; i < 14; i++){
		getsprite(i, teslaSpr);
		spritesetvalue(i, S_WIDTH, 8);
		spritesetvalue(i, S_HEIGHT, 12);
		spritesetvalue(i,S_GRAVITY,0);
		spritesetvalue(i,S_SOLID,0);
	}
	for(i = 16; i < 24; i++){
		getsprite(i, bullet);
		spritesetvalue(i, S_WIDTH, 6);
		spritesetvalue(i, S_HEIGHT, 1);
		spritesetvalue(i, S_GRAVITY, 1);
		spritesetvalue(i, S_SOLID, 1);
		spritesetvalue(i, S_ON_COLLISION, bulletOnCollision);
	}
	memcpy(crossbowSprS, crossbowSpr, 88);
	getsprite(30, crossbowSprS);
	spritesetvalue(30, S_WIDTH, 16);
	spritesetvalue(30, S_HEIGHT, 11);
	spritesetvalue(30, S_SOLID, 0);
	spritesetvalue(30, S_IS_SCROLLED, 0);
}

void addBullet(){
	int s = bulletcount + 16;
	if(ang > 0){
		putsprite(s, x + 10, y + 6);
		spritespeed(s, 16 + arrowangle / 2, 0 - arrowangle * 4);
		spritesetvalue(s,S_ANGLE, 4 - arrowangle * 2);
	}
	else{
		putsprite(s, x - 6, y + 6);
		spritespeed(s, 0 - (16 + arrowangle / 2), arrowangle * 4);
		spritesetvalue(s,S_ANGLE, arrowangle * 2 - 4);
	}
	getsprite(s, bullet);
	spritesetvalue(s, S_WIDTH, 6);
	spritesetvalue(s, S_HEIGHT, 1);
	spritesetvalue(s,S_ON_COLLISION,bulletOnCollision);
	spritesetvalue(s,S_LIVES,0);
	spritesetvalue(s,S_SOLID,1);
	spritesetvalue(s,S_LIVES,8);
	spritesetvalue(s,S_GRAVITY,1);
	bulletcount++;
	if(bulletcount >= 8)
		bulletcount = 0;
}

void drawFlash(int y, int x1, int x2){
	int p1, p2, i;
	if(x2 < x1){
		i = x2;
		x2 = x1;
		x1 = i;
	}
	p1 = y;
	for(i = x1; i < x2; i += 4){
		p2 = y - 4 + random(8);
		line(i, p1, i + 4, p2);
		p1 = p2;
	}
}

void testTesla(int tx, int ty){
	int j;
	if(tx < 0 || tx > 128 || ty < 0 || ty > 128){
		return;
	}
	if(x < tx){
		for(j = x; j < tx; j += 8){
			if(gettileinxy(j, ty)){
				return;
			}
		}
	}
	else{
		for(j = tx; j < x; j += 8){
			if(gettileinxy(j, ty)){
				return;
			}
		}
	}
	spritesetvalue(1, S_SPEEDX, 0);
	spritesetvalue(1, S_SPEEDY, 0);
	for(j = 0; j < 16; j++){
		setcolor(1 + random(2));
		drawFlash(ty, x + 4, tx + 4);
		setcolor(1 + random(2));
		drawFlash(ty, x + 4, tx + 4);
		delayredraw();
		clearscreen();
		drawtile(0 - levelx, 0 - levely);
	}
	game = 0;
	spritesetvalue(1, S_LIVES, 0);
}

void timers(){
	int i, j;
	if(!gettimer(0)){
		settimer(0, 300);
		clearscreen();
		drawtile(0 - levelx, 0 - levely);
		frame = 1 - frame;
		memcpy(crossbowSprS, crossbowSpr, 88);
		getsprite(0, keyspr + 24 * frame);
		for(i = 2; i < 8; i++){
			getsprite(i, zombie[(spritegetvalue(i,S_LIVES) / 3)] + frame * 48);
			if(zombiedir[i - 2] == 1){
				spritesetvalue(i,S_SPEEDX,4);
				spritesetvalue(i,S_FLIP_HORIZONTAL,0);
				if(gettileinxy(spritegetvalue(i,S_X) + 10,spritegetvalue(i,S_Y))){
					zombiedir[i - 2] = 2;
				}
			}
			else if(zombiedir[i - 2] == 2){
				spritesetvalue(i,S_SPEEDX,-4);
				spritesetvalue(i,S_FLIP_HORIZONTAL,1);
				if(gettileinxy(spritegetvalue(i,S_X) - 2,spritegetvalue(i,S_Y))){
					zombiedir[i - 2] = 1;
				}
			}
		}
		for(i = 8; i < 8 + teslacount; i++){
			if((spritegetvalue(i,S_LIVES) > 0) && (spritegetvalue(i,S_Y) < y + 6) && (spritegetvalue(i,S_Y) > y - 6)){
				if((spritegetvalue(i,S_X) < x + 64) && (spritegetvalue(i,S_X) > x - 64)){
					testTesla(spritegetvalue(i,S_X), spritegetvalue(i,S_Y));
				}
			}
		}
		for(i = 16; i < 24; i++){
			if(spritegetvalue(i,S_LIVES) > 1){
				spritesetvalue(i,S_LIVES,spritegetvalue(i,S_LIVES) - 1);
				if(spritegetvalue(i,S_LIVES) == 1){
					spritesetvalue(i,S_LIVES,0);
				}
				if(gettileinxy(spritegetvalue(i, S_X),spritegetvalue(i, S_Y) - 4)){
					spritesetvalue(i, S_SPEEDX, 0);
					spritesetvalue(i, S_SPEEDY, 0);
				}
			}
		}
	}
}

void death(){
	int i;
	spritesetvalue(1,S_LIVES,0);
	for(i = 16; i < 24; i++){
		getsprite(i, meat);
		spritesetvalue(i, S_WIDTH, 2);
		spritesetvalue(i, S_HEIGHT, 2);
		spritesetvalue(i,S_ON_COLLISION,0);
		spritesetvalue(i, S_SPEEDY, 24 + random(12));
		spritesetvalue(i, S_SPEEDX, 10 - random(20));
		putsprite(i, x + random(6), y + random(6));
	}
	setparticle(4, 5, 600);
	setemitter(10, 200, 300, 12);
	emittersize(1, 1, 0);
	drawparticle(x + 4, y + 6, 2);
	for(i = 0; i < 24; i++){
		delayredraw();
	}
}

void drawStringer(int a){
	int i;
	memcpy(crossbowSprS, crossbowSpr, 88);
	for(i = 1; i < 5; i++){
		crossbowSprS[5 - a / i][4 - i] = 0x22;
		crossbowSprS[5 - a / i][6 + i] = 0x22;
	}
	for(i = 8 - a * 2; i < 8; i++){
		crossbowSprS[i][5] = 0x22;
	}
}

void step(){
	x = spritegetvalue(1,S_X);
	y = spritegetvalue(1,S_Y);
	timers();
	key = getkey();
	if(gettileinxy(x,y + 13) || gettileinxy(x + 8,y + 13) || spritegetvalue(1,S_COLLISION) > 0){
		if(key & KEY_B){
			spritesetvalue(1,S_SPEEDY,-40);
		}
		spritesetvalue(1,S_SPEEDX,0);
	} 
	if(key & KEY_LEFT){
		if((!gettileinxy(x - 3,y)) && (!gettileinxy(x - 3, y + 11)))
			spritesetvalue(1,S_SPEEDX,-8);
		else
			spritesetvalue(1,S_X, x + 1);
	}
	else if(key & KEY_RIGHT){
		if((!gettileinxy(x + 10,y)) && (!gettileinxy(x + 10, y + 11)))
			spritesetvalue(1,S_SPEEDX,8);
		else
			spritesetvalue(1,S_X, x - 1);
	}
	if(key & KEY_A && arrowangle < 16){
		arrowangle++;
		drawStringer(arrowangle / 4);
	}
	else if(lastkey & KEY_A && !gettimer(1)){
		settimer(1, 200);
		addBullet();
		arrowangle = 0;
		memcpy(crossbowSprS, crossbowSpr, 88);
	}
	if(spritegetvalue(1,S_SPEEDX) > 0){
		getsprite(1, person + frame * 48);
		spritesetvalue(1,S_FLIP_HORIZONTAL,0);
		ang = 1;
	}
	else if(spritegetvalue(1,S_SPEEDX) < 0){
		getsprite(1, person + frame * 48);
		spritesetvalue(1,S_FLIP_HORIZONTAL,1);
		ang = -1;
	}
	if(gettileinxy(x + 4,y - 2)){
		spritesetvalue(1,S_Y, y + 1);
	}
	if(gettileinxy(x + 4,y + 14) == blades){
		game = 0;
		spritesetvalue(1, S_LIVES, 0);
	}
	if(gettileinxy(x + 10,y + 6) == door || gettileinxy(x - 2,y + 6) == door){
		if(havekey){
			game = 0;
			thislevel++;
		}
		else{
			gotoxy(x / 6, y / 8);
			puts("need key");
		}
	}
	lastkey = key;
}

void draw(){
	scrollscreen();
	scrollscreen();
	setparticle(4, 1, 600);
	setemitter(10, 90, 180, 12);
	emittersize(128, 4, 0);
	drawparticle(0, 0, 1);
	delayredraw();
}

void transition(){
	int i, ix, iy;
	for(i = 0; i < 4; i++){
		for(ix = 0; ix < 32; ix++){
			for(iy = 0; iy < 32; iy++){
				putimage(scrPattern, ix * 4 + i, iy * 4 + i, 4, 4);
			}
		}
		delayredraw();
		delayredraw();
	}
}

void nextLevel(){
	int i;
	for(i = 0; i < 31; i++){
		spritesetvalue(i, S_LIVES, 0);
	}
	transition();
	setbgcolor(4);
	setcolor(1);
	clearscreen();
	gotoxy(7,2);
	puts("level  ");
	putn(thislevel + 1);
	setimagesize(4);
	putimage(wall1, 0, 32, 16, 16);
	putimage(door, 32, 32, 16, 16);
	putimage(wall1, 96, 32, 16, 16);
	for(i = 0; i < 30; i++){
		delayredraw();
	}
	setimagesize(1);
	transition();
	putsprite(30, 2, 2);
	x = 18;
	y = 12;
	levelx = 0;
	levely = 0;
	bulletcount = 0;
	zombiecount = 0;
	teslacount = 0;
	clearscreen();
	loadlevel(levels[thislevel]);
	loadtile(tile, 16, 16, 32, 16);
	drawtile(0, 0);
	settimer(0, 300);
}

void endGame(){
	int i;
	transition();
	clearscreen();
	for(i = 0; i < 31; i++){
		spritesetvalue(i, S_LIVES, 0);
	}
	putimagerle(endscrSpr, 1, 32, 126, 89);
	gotoxy(2, 1);
	puts("You found the cure and saved your settlement.");
	settimer(0, 1000);
	while(gettimer(0)){}
	while(!getkey()){}
	init();
}

int main(){
	init();
	while(1){
		nextLevel();
		while(game){
			step();
			draw();
		}
		if(!spritegetvalue(1,S_LIVES)){
			death();
		}
		if(thislevel > maxlevel){
			endGame();
		}
	}
}