void drawHealth(){
	int i;
	for(i = 1; i < health; i++){
		healthbar[15 + i] = 0xaa;
		healthbar[31 + i] = 0xaa;
	}
	i = health;
	for(;i < 17;i++){
		healthbar[15 + i] = 0x0;
		healthbar[31 + i] = 0x0;
	}
}

void chartosprite(int x, int y, int n){
	int i,j;
	for(i = 0; i < 2; i++)
		for(j = 0; j < 5; j++)
			fuelSprt[x + i + (y + j) * 10] = guichr[n * 10 + i + j * 2];
}

void printtosprite(int x, int y, int n){
	int i;
	for(i = 2; i >= 0; i--){
		chartosprite(x + i * 2, y, n % 10); 
		n = n / 10;
	}
}

int getXonScreen(int x, int y){
	return ((x - 8) * (y + 4) + (( 12 - y) * angle / 3));
}

void drawTree(){
	treey += speed;
	if(treey > 255){
		treey = 0;
		if(random(32) > angle + 16){
			treex = 14;
		}
		else{
			treex = 2;
		}
	}
	getsprite(4, tree[treey / 18]);
	putsprite(4,48 + getXonScreen(treex, treey / 12) ,64 + treey/5);
}

void drawOpponent(){
	opponenty += speed - 5;
	if(opponenty > 255){
		opponenty = 0;
		if(random(32) > angle + 16){
			opponentx = 4;
		}
		else{
			opponentx = 10;
		}
	}
	else if(opponenty < -6){
		opponenty = 255;
		if(random(32) > angle + 16){
			opponentx = 4;
		}
		else{
			opponentx = 10;
		}
	}
	getsprite(6, opcar[opponenty / 30]);
	putsprite(6,62 + getXonScreen(opponentx, opponenty / 22 - 2) - angle ,68 + opponenty/4);
}

void drawFuel(){
	fuely += speed;
	if(fuely > 255 && nextfuel <= 0){
		nextfuel = 2000 + random(1000);
		fuely = 0;
		if(random(32) > angle + 16){
			fuelx = 4;
		}
		else{
			fuelx = 10;
		}
	}
	nextfuel -= speed;
	getsprite(5, fuel[fuely / 30]);
	putsprite(5,62 + getXonScreen(fuelx, fuely / 22 - 2) - angle ,68 + fuely/4);

}

void drawRoad(){
	int i,rx;
	setimagesize(4);
	putimagerle(ground[cadr],0,64,32,16);
	for(i = 8; i < 16; i++){
		rx = (16 - i) * angle / 4;
		putimage(road[cadr] + i * 16, rx + 2,64 + i * 4,32,1);
	}
	setimagesize(2);
	for(i = 8; i < 16; i++){
		rx = (16 - i) * angle / 4 + angle * 2;
		putimage(road[cadr] + i * 16, rx + 33,64 + i * 2,32,1);
	}
	setimagesize(1);
	for(i = 0; i < 16; i++){
		rx = (16 - i) * angle / 4 + angle * 4;
		putimage(road[cadr] + i * 16, rx + 49,64 + i,32,1);
	}
}

void randomEvent(){
	if(gettimer(1) == 0){
		settimer(1, 8000 + random(5000));
		settimer(2, 3000);
		angle = 1 - random(2);
	}
	if(speed){
		if(gettimer(2)){
			if(angle > 0 && angle < 16)
				angle++;
			if(angle < 0 && angle > -16)
				angle--;
		}
		else if(angle < 0){
			angle++;
		}
		else if(angle > 0){
			angle--;
		}
	}
}

void step(){
	key = getkey();
	if(key & KEY_LEFT && carx > 0)
		carx -= 2;
	if(key & KEY_RIGHT && carx < 42)
		carx += 2;
	if(key & KEY_DOWN && speed > 0)
		speed--;
	if(key & KEY_UP && speed < 16)
		speed++;
	if(angle > -5)
		spritesetvalue(2,S_X,20 + angle * 8);
	else
		spritesetvalue(2,S_X,160 + angle * 8);
	if(gettimer(0) == 0 && speed > 0){
		settimer(0, 200 - speed * 12);
		cadr++;
		distancem += (speed / 2);
		if(distancem >= 1000){
			distancem -= 1000;
			distancekm++;
		}
		if(cadr > 2)
			cadr = 0;
		if(carx < 2 || carx > 41)
			speed--;
		if(angle < -3 && carx < 42)
			carx++;
		else if(angle > 3 && carx > 0)
			carx--;
	}
	if(gettimer(4) == 0){
		settimer(4,500);
		if(fuellevel > 0)
			fuellevel--;
		else
			game = 0;
	}
	if(opponenty > 160){
		if(spritegetvalue(30, S_COLLISION) == 6){
			tone(30,500);
			health -= speed/4;
			drawHealth();
			if(health <= 0)
				game = 0;
			speed = 0;
		}
	}
	if(fuely > 150){
		if(spritegetvalue(5, S_COLLISION) == 30){
			fuellevel = fuellevel + 10;
			if(fuellevel > 100)
				fuellevel = 100;
			fuely = 300;
			tone(1500,100);
		}
	}
	spritesetvalue(3,S_X,80 + angle * 8);
	getsprite(31,speedometer[speed/4]);
	putsprite(30,8 + carx * 2,110);
	printtosprite(2, 10,fuellevel);
	gotoxy(1,0);
	printf("%d km %d m  ", distancekm, distancem);
	if(angle < -3){
		spritesetvalue(30,S_FLIP_HORIZONTAL,1);
		getsprite(30,carturn);
	}
	else if(angle > 3){
		spritesetvalue(30,S_FLIP_HORIZONTAL,0);
		getsprite(30,carturn);
	}
	else{
		spritesetvalue(30,S_FLIP_HORIZONTAL,1);
		getsprite(30,car);
	}
}

